// Clean up Gemini response and extract JSON
const results = [];

for (const item of $input.all()) {
  try {
    // Get the raw Gemini response
    const geminiResponse = item.json.content.parts[0].text;

    // More aggressive cleaning for Gemini's format
    let cleanedResponse = geminiResponse
      .replace(/```json\n/g, '')
      .replace(/\n```/g, '')
      .replace(/```/g, '')
      .replace(/^['"`]+|['"`]+$/g, '') // Remove quotes/backticks at start/end
      .trim();

    // Handle the \n escape characters by parsing as JSON string first
    try {
      // If it's a JSON string (wrapped in quotes), parse it
      cleanedResponse = JSON.parse('"' + cleanedResponse + '"');
    } catch (e) {
      // If that fails, just use the cleaned response as-is
    }

    console.log('Final cleaned response:', cleanedResponse); // Debug line

    // Parse the cleaned JSON
    const parsedData = JSON.parse(cleanedResponse);


    // Format for matrix
    results.push({
      json: {

        name: parsedData.Name || "candidateName",
        email: parsedData["Email address"] || '',
        mobile: parsedData["Mobile Number"] || '',
        qualifications: Array.isArray(parsedData.qualifications) 
          ? '• ' + parsedData.qualifications.join('\n• ') 
          : parsedData.qualifications,
        career_history: Array.isArray(parsedData.career_history) 
          ? parsedData.career_history.join('\n\n') 
          : parsedData.career_history,
        comments: '',
        processed_date: new Date().toISOString(),
        //raw_gemini_response: geminiResponse, // Keep for debugging
        //cleaned_response: cleanedResponse // Keep for debugging
      }
    });
  } catch (error) {
    console.log('Error processing item:', error);
    console.log('Raw response:', item.json);
   // console.log('Cleaned response attempt:', cleanedResponse);
    // Return error info for debugging
    results.push({
      json: {
        error: true,
        error_message: error.message,
        raw_response: item.json,
        cleaned_attempt: cleanedResponse || 'Failed to clean'
      }
    });
  }
}

return results;
